    console.log('üìÖ Date changed:', { newStartDate, newEndDate });
    setDates(newStartDate, newEndDate); // No page refresh
  }, [setDates]);

  return (
    <div>
      <ProductInfo product={product} />
      <EnhancedBookingForm 
        item={product}
        type="product"
        onDateChange={handleDateChange} // Dates persist after this call
      />
    </div>
  );
}
```

