import { useEffect } from "react";

type Props = {
  /** kalau true, mainkan animasi keluar lalu panggil onExited */
  closing?: boolean;
  onExited?: () => void;
};

const EXIT_MS = 700; // samakan dengan durasi animate-fadeOut

export default function FullScreenLoader({ closing = false, onExited }: Props) {
  useEffect(() => {
    if (!closing) return;
    const t = setTimeout(() => onExited?.(), EXIT_MS);
    return () => clearTimeout(t);
  }, [closing, onExited]);

  return (
    <div
      className={`fixed inset-0 z-[9999] flex items-center justify-center
         bg-navy-blue-950 text-gray-100
         ${closing ? "animate-fadeOut pointer-events-none" : ""}`}
      aria-busy={!closing}
      aria-live="polite"
    >
      {/* container 80x50 ala spesifikasi kamu */}
      <div className="relative w-20 h-[50px] flex items-center justify-center">
        {/* .loader-text */}
        <span className="absolute top-0 text-navy-blue-800 text-[1.5rem] tracking-[1px] animate-textPulse">
          loading
        </span>

        {/* .load (progress bar) */}
        <span className="absolute bottom-0 left-0 right-0 h-[4px] bg-navy-blue-900 overflow-hidden rounded">
          <span className="block h-full w-1/2 bg-navy-blue-800 animate-loaderSlide" />
        </span>
      </div>
    </div>
  );
}

import React from 'react';
import LoadingSpinner from './LoadingSpinner';

interface LoadingButtonProps {
  isLoading?: boolean;
  disabled?: boolean;
  type?: 'button' | 'submit' | 'reset';
  variant?: 'primary' | 'secondary' | 'accent' | 'outline' | 'ghost';
  size?: 'sm' | 'md' | 'lg';
  fullWidth?: boolean;
  rounded?: boolean;
  onClick?: () => void;
  children: React.ReactNode;
  className?: string;
}

const LoadingButton: React.FC<LoadingButtonProps> = ({
  isLoading = false,
  disabled = false,
  type = 'button',
  variant = 'primary',
  size = 'md',
  fullWidth = false,
  rounded = false,
  onClick,
  children,
  className = ''
}) => {
  // Base classes
  const baseClasses = 'relative font-medium shadow transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-offset-2';

  // Size classes
  const sizeClasses = {
    sm: 'py-1 px-3 text-xs',
    md: 'py-2 px-4 text-sm',
    lg: 'py-3 px-6 text-base'
  };

  // Variant classes - updated for new navy blue palette
  const variantClasses = {
    primary: 'bg-pop-primary text-navy-blue-50 hover:bg-pop-hover focus:ring-navy-blue-800/50',
    secondary: 'bg-navy-blue-500 text-navy-blue-50 hover:bg-navy-blue-600 focus:ring-navy-blue-500/50',
    accent: 'bg-navy-blue-800 text-navy-blue-50 hover:bg-navy-blue-950 focus:ring-navy-blue-800/50',
    outline: 'bg-transparent border border-navy-blue-800 text-navy-blue-800 hover:bg-navy-blue-800/10 focus:ring-navy-blue-800/30',
    ghost: 'bg-transparent hover:bg-navy-blue-50 text-navy-blue-950 hover:text-navy-blue-800 focus:ring-navy-blue-800/20'
  };

  // Width and rounded classes
  const widthClass = fullWidth ? 'w-full' : '';
  const roundedClass = rounded ? 'rounded-full' : 'rounded-lg';

  // Disabled classes
  const disabledClasses = (disabled || isLoading) ? 'opacity-70 cursor-not-allowed' : 'hover:translate-y-[-2px] hover:shadow-md';

  // Combine all classes
  const buttonClasses = `
    ${baseClasses}
    ${sizeClasses[size]}
    ${variantClasses[variant]}
    ${widthClass}
    ${roundedClass}
    ${disabledClasses}
    ${className}
  `;

  return (
    <button
      type={type}
      className={buttonClasses}
      onClick={onClick}
      disabled={disabled || isLoading}
    >
      {isLoading ? (
        <>
          <span className="opacity-0">{children}</span>
          <span className="absolute inset-0 flex items-center justify-center">
            <LoadingSpinner 
              type="spinner" 
              size="sm" 
              color={variant === 'outline' || variant === 'ghost' ? 'primary' : 'light'} 
            />
          </span>
        </>
      ) : children}
    </button>
  );
};

export default LoadingButton;

