            <div className="space-y-2">
              <div className="h-2 md:h-2.5 bg-light rounded-full w-2/3 animate-pulse" />
              <div className="h-6 md:h-8 bg-light rounded-full w-full animate-pulse" />
            </div>
          </div>
        </div>
      );
    }
  };

  // Render multiple skeletons
  return (
    <>
      {Array.from({ length: count }).map((_, index) => renderSkeleton(index))}
    </>
  );
};

export default LoadingCard;

import React from 'react';
import LoadingSpinner from './LoadingSpinner';

interface LoadingOverlayProps {
  isLoading: boolean;
  message?: string;
  spinnerType?: 'spinner' | 'dots' | 'pulse';
  spinnerSize?: 'sm' | 'md' | 'lg';
  spinnerColor?: 'primary' | 'secondary' | 'accent' | 'light' | 'dark';
  fullScreen?: boolean;
  transparent?: boolean;
  children?: React.ReactNode;
}

const LoadingOverlay: React.FC<LoadingOverlayProps> = ({
  isLoading,
  message = 'Memuat...',
  spinnerType = 'spinner',
  spinnerSize = 'md',
  spinnerColor = 'primary',
  fullScreen = false,
  transparent = false,
  children
}) => {
  if (!isLoading) return <>{children}</>;

  const overlayClasses = `
    flex flex-col items-center justify-center
    ${fullScreen ? 'fixed inset-0 z-50' : 'absolute inset-0 z-10'}
    ${transparent ? 'bg-white/70 backdrop-blur-sm' : 'bg-white'}
    transition-all duration-300
  `;

  return (
    <div className="relative">
      {children}

      <div className={overlayClasses}>
        <LoadingSpinner 
          type={spinnerType} 
          size={spinnerSize} 
          color={spinnerColor} 
        />

        {message && (
          <p className="mt-4 text-dark font-medium animate-pulse">
            {message}
          </p>
        )}
      </div>
    </div>
  );
};

export default LoadingOverlay;

import React from "react";

interface LoadingSpinnerProps {
  size?: "sm" | "md" | "lg";
  color?: "primary" | "secondary" | "accent" | "light" | "dark";
  type?: "spinner" | "dots" | "pulse";
}

const LoadingSpinner: React.FC<LoadingSpinnerProps> = ({
  size = "md",
  color = "primary",
  type = "spinner",
}) => {
  // Size classes
  const sizeClasses = {
    sm: "w-4 h-4",
    md: "w-8 h-8",
    lg: "w-12 h-12",
  };

  // Color classes
  const colorClasses = {
    primary: "text-primary",
    secondary: "text-secondary",
    accent: "text-accent",
    light: "text-light",
    dark: "text-dark",
  };

  // Render spinner type
  if (type === "spinner") {
    return (
      <div
        className={`${sizeClasses[size]} ${colorClasses[color]} animate-spin`}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          className="w-full h-full"
        >
          <circle
            className="opacity-25"
            cx="12"
            cy="12"
            r="10"
            stroke="currentColor"
            strokeWidth="4"
          ></circle>
          <path
            className="opacity-75"
            fill="currentColor"
            d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
          ></path>
        </svg>
      </div>
    );
  }

  // Render dots type
  if (type === "dots") {
    return (
      <div className="flex space-x-1 justify-center items-center">
        <div
          className={`${sizeClasses[size]
            .split(" ")[0]
            .replace("w-", "w-2 ")} ${
            colorClasses[color]
          } rounded-full animate-bounce`}
          style={{ animationDelay: "0ms" }}
        ></div>
        <div
          className={`${sizeClasses[size]
            .split(" ")[0]
            .replace("w-", "w-2 ")} ${
            colorClasses[color]
          } rounded-full animate-bounce`}
          style={{ animationDelay: "150ms" }}
        ></div>
        <div
          className={`${sizeClasses[size]
            .split(" ")[0]
            .replace("w-", "w-2 ")} ${
            colorClasses[color]
          } rounded-full animate-bounce`}
          style={{ animationDelay: "300ms" }}
        ></div>
      </div>
    );
  }

  // Render pulse type
  return (
    <div
      className={`${sizeClasses[size]} ${colorClasses[color]} rounded-full animate-pulse flex items-center justify-center`}
    >
      <div className="w-3/4 h-3/4 rounded-full bg-current opacity-75"></div>
    </div>
  );
};

