import { useState, useEffect, useRef, memo, useCallback, useMemo } from "react";
import { Link, useNavigate } from "react-router-dom";
import { STORAGE_BASE_URL } from "../api/constants";
import axiosInstance from "../api/axiosInstance";
import FloatingCartButton from "./FloatingCartButton";

const NavCard = memo(function NavCard() {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState<any[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const navigate = useNavigate();
  const inputRef = useRef<HTMLInputElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // Memoized fetch function to prevent recreation on every render
  const fetchSuggestions = useCallback(async (searchQuery: string) => {
    try {
      // Fetch suggestions dari multiple sources
      const [productRes, bundlingRes] = await Promise.allSettled([
        axiosInstance.get("/search-suggestions", {
          params: { q: searchQuery, limit: 10 },
        }),
        axiosInstance.get("/bundlings", {
          params: { q: searchQuery, limit: 8 },
        }),
      ]);

      let allSuggestions: any[] = [];

      // Add product suggestions
      if (
        productRes.status === "fulfilled" &&
        productRes.value.data.suggestions
      ) {
        allSuggestions = [...productRes.value.data.suggestions];
      }

      // Add bundling suggestions
      if (bundlingRes.status === "fulfilled" && bundlingRes.value.data.data) {
        const bundlingSuggestions = bundlingRes.value.data.data.map(
          (bundling: any) => ({
            display: `üì¶ ${bundling.name}`,
            url: `/bundling/${bundling.slug}`,
            thumbnail:
              bundling.bundlingPhotos?.[0]?.photo ||
              bundling.products?.[0]?.productPhotos?.[0]?.photo,
            type: "bundling",
          })
        );
        allSuggestions = [...allSuggestions, ...bundlingSuggestions];
      }

      // Limit total suggestions to 15 (increased from 12)
      setSuggestions(allSuggestions.slice(0, 15));
    } catch (err) {
      console.error("Search suggestions error:", err);
      setSuggestions([]);
    }
  }, []);

  // Ambil saran (products + bundlings) with optimized debouncing
  useEffect(() => {
    if (query.length < 2) {
      setSuggestions([]);
      return;
    }

    const debounce = setTimeout(() => fetchSuggestions(query), 400); // Increased debounce
    return () => clearTimeout(debounce);
  }, [query, fetchSuggestions]);

  // Klik di luar ‚Üí tutup dropdown
  useEffect(() => {
    const handleClickOutside = (e: MouseEvent) => {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(e.target as Node) &&
        inputRef.current &&
        !inputRef.current.contains(e.target as Node)
      ) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleSearch = useCallback(
    (e: React.FormEvent) => {
      e.preventDefault();
      if (query.trim()) {
        navigate(`/browse-product?q=${encodeURIComponent(query)}`);
        setShowSuggestions(false);
      }
    },
    [query, navigate]
  );

  const selectSuggestion = useCallback(
    (url: string) => {
      navigate(url);
      setQuery("");
      setShowSuggestions(false);
    },
    [navigate]
  );

  const handleInputChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      const value = e.target.value;
      setQuery(value);
      if (value.length > 0) {
        setShowSuggestions(true);
      } else {
        setShowSuggestions(false);
      }
    },
    []
  );

  const handleInputFocus = useCallback(() => {
    if (query.length > 0) setShowSuggestions(true);
  }, [query.length]);

  return (
    <header
      className="w-full fixed inset-x-0 top-0 z-40 mx-auto max-w-screen-2xl border border-support-subtle 
    bg-base-light-primary/70 px-2 lg:px-3 py-2 lg:py-3 shadow-lg backdrop-blur-lg lg:top-6 lg:rounded-3xl transition-all duration-300"
    >
      <div className="px-2 lg:px-4">
        <nav
          className="flex items-center justify-between gap-2 lg:gap-4"
          aria-label="Main navigation"
        >
          <div className="flex shrink-0">
            <Link
              to="/"
              className="flex items-center gap-1 focus-visible:ring-2 focus-visible:ring-primary rounded-lg"
            >
              <img
                src={`${STORAGE_BASE_URL}/LOGO-GPR.png`}
                alt="Global Photo Rental Logo"
                className="h-12 w-auto"
              />

              <span className="text-lg lg:text-2xl font-bold text-support-light-primary">
                Global
              </span>
              <span className="text-lg lg:text-2xl font-bold text-support-primary hidden sm:block">
                Photo Rental
              </span>
              <span className="text-lg lg:text-2xl font-bold text-support-primary sm:hidden">
                PR
              </span>
            </Link>
          </div>
          <div className="hidden lg:flex lg:items-center lg:justify-center lg:gap-5">
            <Link
              className="inline-block rounded-lg px-3 py-2 text-lg font-medium text-support-primary transition-all duration-300 hover:bg-base-light-primary hover:text-pop-primary hover:shadow-md hover:translate-y-[-2px] focus-visible:ring-2 focus-visible:ring-pop-primary"
              to="/browse-product"
            >
              Kategori
            </Link>
            <Link
              className="inline-block rounded-lg px-3 py-2 text-lg font-medium text-support-primary transition-all duration-300 hover:bg-base-light-primary hover:text-pop-primary hover:shadow-md hover:translate-y-[-2px] focus-visible:ring-2 focus-visible:ring-pop-primary"
              to="/cara-sewa"
            >
              Cara Sewa
            </Link>
            <a
              className="inline-block rounded-lg px-3 py-2 text-lg font-medium text-support-primary transition-all duration-300 hover:bg-base-light-primary hover:text-pop-primary hover:shadow-md hover:translate-y-[-2px] focus-visible:ring-2 focus-visible:ring-pop-primary"
              href="https://admin.globalphotorental.com/register"
              target="_blank"
              rel="noopener noreferrer"
            >
              Registrasi
            </a>
          </div>
          <div className="flex flex-1 justify-end items-center gap-2 lg:gap-4">
            <form
              onSubmit={handleSearch}
              className="flex-1 max-w-[320px] lg:max-w-[600px] relative"
              role="search"
            >
              <div className="relative">
                <input
                  ref={inputRef}
                  type="text"
                  name="q"
                  value={query}
                  onChange={handleInputChange}
                  onFocus={handleInputFocus}
                  className="w-full border border-support-subtle h-10 lg:h-12 shadow-sm px-3 lg:px-4 py-2 rounded-full text-support-primary placeholder:text-support-tertiary focus:outline-none focus:ring-2 focus:ring-pop-primary/40 focus:border-pop-primary text-sm lg:text-base transition-all duration-300 focus:shadow-md bg-background-light-card"
                  placeholder="Cari..."
                  aria-label="Cari Produk atau Kategori"
                />
                <button
                  type="submit"
                  className="absolute top-2.5 lg:top-3.5 right-2 lg:right-3 text-support-tertiary transition-all duration-300 hover:text-pop-primary hover:scale-110"
                  aria-label="Cari"
                >
                  <svg
                    className="h-4 w-4 lg:h-5 lg:w-5 fill-current"
                    viewBox="0 0 56.966 56.966"
                  >
                    <path d="M55.146,51.887L41.588,37.786c3.486-4.144,5.396-9.358,5.396-14.786c0-12.682-10.318-23-23-23s-23,10.318-23,23  s10.318,23,23,23c4.761,0,9.298-1.436,13.177-4.162l13.661,14.208c0.571,0.593,1.339,0.92,2.162,0.92  c0.779,0,1.518-0.297,2.079-0.837C56.255,54.982,56.293,53.08,55.146,51.887z M23.984,6c9.374,0,17,7.626,17,17s-7.626,17-17,17  s-17-7.626-17-17S14.61,6,23.984,6z" />
                  </svg>
                </button>
              </div>

              {/* Autocomplete Dropdown */}
              {showSuggestions && suggestions.length > 0 && (
                <div
                  ref={dropdownRef}
                  className="absolute top-full left-0 right-0 mt-1 bg-base-secondary border border-support-subtle rounded-lg shadow-lg z-50 max-h-64 lg:max-h-80 overflow-y-auto"
                >
                  {suggestions.map((item, index) => (
                    <button
                      key={index}
                      type="button"
                      onClick={() => selectSuggestion(item.url)}
                      className={`
                        w-full text-left px-3 lg:px-4 py-2 hover:bg-base-tertiary flex items-center gap-2 lg:gap-3 text-xs lg:text-sm transition-all duration-300 hover:pl-4 lg:hover:pl-5 first:rounded-t-lg last:rounded-b-lg
                        ${
                          item.type === "bundling"
                            ? "border-l-2 border-l-blue-400"
                            : ""
                        }
                      `}
                    >
                      {item.thumbnail && (
                        <img
                          src={`${STORAGE_BASE_URL}/${item.thumbnail}`}
                          alt=""
                          className="w-6 h-6 lg:w-8 lg:h-8 object-cover rounded flex-shrink-0"
                        />
                      )}
                      <span
                        className={`truncate ${
                          item.type === "bundling"
                            ? "text-blue-700 font-medium"
                            : "text-support-primary"
                        }`}
                      >
                        {item.display}
                      </span>
                      {item.type === "bundling" && (
                        <span className="text-xs text-blue-500 ml-auto px-1 py-0.5 bg-blue-50 rounded">
                          Bundling
                        </span>
                      )}
                    </button>
                  ))}
                </div>
              )}
            </form>
            <div className="flex-shrink-0">
              <FloatingCartButton />
            </div>
          </div>

          <div className="flex items-center flex-shrink-0">
            {/* Dark mode toggle button removed */}
          </div>
        </nav>
      </div>
    </header>
  );
});

export default NavCard;

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { PhotoIcon, ExclamationTriangleIcon } from '@heroicons/react/24/outline';
import { validateProductPhotoUrl } from '../utils/productValidation';

interface OptimizedImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
  quality?: number;
  format?: 'webp' | 'avif' | 'original';
  placeholder?: 'blur' | 'skeleton' | 'none';
  blurDataURL?: string;
  priority?: boolean;
  onLoad?: () => void;
  onError?: () => void;
  sizes?: string;
  objectFit?: 'cover' | 'contain' | 'fill' | 'none' | 'scale-down';
  fallbackSrc?: string;
}

interface ImageState {
  isLoading: boolean;
  hasError: boolean;
  isInView: boolean;
  currentSrc: string;
  retryCount: number;
  validationStatus: 'pending' | 'valid' | 'invalid';
  isEmpty: boolean;
}

const OptimizedImage: React.FC<OptimizedImageProps> = ({
  src,
  alt,
  width,
  height,
  className = '',
  quality = 75,
  format = 'webp',
  placeholder = 'blur',
  blurDataURL,
  priority = false,
  onLoad,
  onError,
  sizes,
  objectFit = 'cover',
  fallbackSrc
}) => {
  const [state, setState] = useState<ImageState>({
    isLoading: true,
    hasError: false,
    isInView: false,
    currentSrc: '',
    retryCount: 0,
    validationStatus: 'pending',
    isEmpty: false
  });

  const imgRef = useRef<HTMLImageElement>(null);
  const observerRef = useRef<IntersectionObserver>();

  // Simulate CDN URL generation with optimizations
  const generateOptimizedSrc = useCallback((originalSrc: string, options: {
    width?: number;
    height?: number;
    quality?: number;
    format?: string;
  }) => {
    // In a real scenario, this would generate CDN URLs like Cloudinary, ImageKit, etc.
    // For now, we'll simulate with query parameters that could work with a CDN
    const url = new URL(originalSrc, window.location.origin);
    
    if (options.width) url.searchParams.set('w', String(options.width));
    if (options.height) url.searchParams.set('h', String(options.height));
    if (options.quality) url.searchParams.set('q', String(options.quality));
    if (options.format && options.format !== 'original') {
      url.searchParams.set('f', options.format);
    }
    
    // Add compression and optimization flags
    url.searchParams.set('auto', 'compress');
    url.searchParams.set('cs', 'srgb');
    
    return url.toString();
  }, []);

  // Generate responsive srcSet for different screen densities
  const generateSrcSet = useCallback((originalSrc: string) => {
    if (!width) return '';
    
    const densities = [1, 1.5, 2, 3];
    return densities
      .map(density => {
        const optimizedSrc = generateOptimizedSrc(originalSrc, {
          width: Math.round((width || 400) * density),
          height: height ? Math.round(height * density) : undefined,
          quality: density > 2 ? 60 : quality, // Lower quality for high-density screens
          format
        });
        return `${optimizedSrc} ${density}x`;
      })
      .join(', ');
  }, [width, height, quality, format, generateOptimizedSrc]);

  // Intersection Observer for lazy loading
  useEffect(() => {
    if (priority) {
      setState(prev => ({ ...prev, isInView: true }));
      return;
    }

    if (!imgRef.current) return;

    observerRef.current = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setState(prev => ({ ...prev, isInView: true }));
          observerRef.current?.disconnect();
        }
      },
      {
        rootMargin: '50px', // Start loading 50px before the image comes into view
        threshold: 0.1
      }
    );

    observerRef.current.observe(imgRef.current);

    return () => {
      observerRef.current?.disconnect();
    };
  }, [priority]);

  // Validate and generate optimized image sources when in view
  useEffect(() => {
    if (!state.isInView) return;

    // Check if src is empty or invalid
    if (!src || src.trim() === '') {
      console.warn('üì∑ [OptimizedImage] Empty or invalid src provided');
      setState(prev => ({ 
        ...prev, 
        isEmpty: true,
        validationStatus: 'invalid',
        hasError: true,
        isLoading: false
      }));
      return;
    }

    const optimizedSrc = generateOptimizedSrc(src, {
      width,
      height,
      quality,
      format
    });

    // Validate the URL before setting it
    validateProductPhotoUrl(src).then(isValid => {
      setState(prev => ({ 
        ...prev, 
        currentSrc: optimizedSrc,
        validationStatus: isValid ? 'valid' : 'invalid',
        isEmpty: false
      }));
      
      if (!isValid) {
        console.warn('üì∑ [OptimizedImage] Photo validation failed:', { src, optimizedSrc });
      }
    }).catch(error => {
      console.error('üì∑ [OptimizedImage] Photo validation error:', error);
      setState(prev => ({ 
        ...prev, 
        validationStatus: 'invalid',
        hasError: true,
        isLoading: false
      }));
    });
  }, [state.isInView, src, width, height, quality, format, generateOptimizedSrc]);

  // Handle image load success
  const handleLoad = useCallback(() => {
    setState(prev => ({ ...prev, isLoading: false, hasError: false }));
    onLoad?.();
  }, [onLoad]);

  // Handle image load error with retry mechanism
  const handleError = useCallback(() => {
    console.error('üì∑ [OptimizedImage] Image load failed:', {
      src: state.currentSrc,
      alt,
      retryCount: state.retryCount,
      validationStatus: state.validationStatus
    });
    
    setState(prev => {
      const newRetryCount = prev.retryCount + 1;
      
      // Try fallback src if available and this is first retry
      if (fallbackSrc && newRetryCount === 1) {
        console.log('üì∑ [OptimizedImage] Trying fallback src:', fallbackSrc);
        return {
          ...prev,
          currentSrc: fallbackSrc,
          retryCount: newRetryCount,
          validationStatus: 'pending'
        };
      }
      
      // Give up after 3 retries
      if (newRetryCount >= 3) {
        console.warn('üì∑ [OptimizedImage] Max retries reached, giving up');
        return {
          ...prev,
          hasError: true,
          isLoading: false,
          validationStatus: 'invalid'
        };
      }
      
      // Retry with original source
      setTimeout(() => {
        console.log('üì∑ [OptimizedImage] Retrying with lower quality');
        setState(current => ({
          ...current,
          currentSrc: generateOptimizedSrc(src, { width, height, quality: 60 }) // Lower quality on retry
        }));
      }, 1000 * newRetryCount); // Exponential backoff
      
      return { ...prev, retryCount: newRetryCount };
    });
    
    onError?.();
  }, [fallbackSrc, src, width, height, quality, generateOptimizedSrc, onError, state.currentSrc, state.retryCount, state.validationStatus, alt]);

  // Retry function for manual retry
  const handleRetry = useCallback(() => {
    setState(prev => ({
      ...prev,
      isLoading: true,
      hasError: false,
      retryCount: 0,
      currentSrc: generateOptimizedSrc(src, { width, height, quality })
    }));
  }, [src, width, height, quality, generateOptimizedSrc]);

  // Generate blur placeholder
  const getPlaceholderStyle = useCallback(() => {
    if (placeholder === 'none') return {};
    
    if (blurDataURL) {
      return {
        backgroundImage: `url(${blurDataURL})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        filter: 'blur(10px)'
      };
    }
    
    if (placeholder === 'blur') {
      return {
        background: 'linear-gradient(45deg, #f3f4f6 25%, transparent 25%), linear-gradient(-45deg, #f3f4f6 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #f3f4f6 75%), linear-gradient(-45deg, transparent 75%, #f3f4f6 75%)',
        backgroundSize: '20px 20px',
        backgroundPosition: '0 0, 0 10px, 10px -10px, -10px 0px'
      };
    }
    
    return {};
  }, [placeholder, blurDataURL]);

  // Base container styles
  const containerClasses = `relative overflow-hidden ${className}`;
  const imageClasses = `transition-opacity duration-500 ${
    state.isLoading ? 'opacity-0' : 'opacity-100'
  } ${objectFit === 'cover' ? 'object-cover' : objectFit === 'contain' ? 'object-contain' : `object-${objectFit}`} w-full h-full`;

  return (
    <div ref={imgRef} className={containerClasses} style={{ width, height }}>
      {/* Placeholder/Skeleton */}
      {(state.isLoading || !state.isInView) && (
        <div
          className="absolute inset-0 bg-gray-200 flex items-center justify-center"
          style={getPlaceholderStyle()}
        >
          {placeholder === 'skeleton' ? (
            <div className="animate-pulse bg-gray-300 w-full h-full" />
          ) : (
            <PhotoIcon className="w-8 h-8 text-gray-400" />
          )}
        </div>
      )}

      {/* Error State */}
      {(state.hasError || state.isEmpty || state.validationStatus === 'invalid') && (
        <div className="absolute inset-0 bg-gray-100 flex flex-col items-center justify-center p-4">
          <ExclamationTriangleIcon className="w-8 h-8 text-gray-400 mb-2" />
          <p className="text-xs text-gray-500 text-center mb-2">
            {state.isEmpty ? 'Tidak ada gambar' : 
             state.validationStatus === 'invalid' ? 'Gambar tidak valid' :
             'Gagal memuat gambar'}
          </p>
          {!state.isEmpty && state.retryCount < 3 && (
            <button
              onClick={handleRetry}
              className="text-xs bg-navy-blue-600 text-white px-3 py-1 rounded hover:bg-navy-blue-700 transition-colors"
            >
              Coba Lagi ({3 - state.retryCount} tersisa)
            </button>
          )}
        </div>
      )}

      {/* Optimized Image */}
      {state.isInView && state.currentSrc && !state.hasError && !state.isEmpty && state.validationStatus !== 'invalid' && (
        <picture>
          {/* WebP source for modern browsers */}
          {format === 'webp' && (
            <source
              srcSet={generateSrcSet(src.replace(/\.(jpg|jpeg|png)$/i, '.webp'))}
              sizes={sizes}
              type="image/webp"
            />
          )}
          
          {/* AVIF source for cutting-edge browsers */}
          {format === 'avif' && (
            <source
              srcSet={generateSrcSet(src.replace(/\.(jpg|jpeg|png|webp)$/i, '.avif'))}
              sizes={sizes}
              type="image/avif"
            />
          )}
          
          {/* Fallback image */}
          <img
            src={state.currentSrc}
            srcSet={generateSrcSet(src)}
            sizes={sizes}
            alt={alt}
            className={imageClasses}
            onLoad={handleLoad}
            onError={handleError}
            loading={priority ? 'eager' : 'lazy'}
            decoding="async"
            style={{
              ...(width && { maxWidth: width }),
              ...(height && { maxHeight: height })
            }}
          />
        </picture>
      )}

      {/* Loading indicator for priority images */}
      {priority && state.isLoading && (
        <div className="absolute top-2 right-2">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-navy-blue-600 bg-white/80 backdrop-blur-sm"></div>
        </div>
      )}
    </div>
  );
};

// Utility hook for preloading images
export const useImagePreloader = () => {
  const preloadImage = useCallback((src: string, options?: { format?: string; quality?: number }) => {
    return new Promise<void>((resolve, reject) => {
      const img = new Image();
      
      // Generate optimized URL if options provided
      const optimizedSrc = options ? (() => {
        const url = new URL(src, window.location.origin);
        if (options.quality) url.searchParams.set('q', String(options.quality));
        if (options.format) url.searchParams.set('f', options.format);
        return url.toString();
      })() : src;
      
      img.onload = () => resolve();
      img.onerror = reject;
      img.src = optimizedSrc;
    });
  }, []);

  const preloadImages = useCallback((srcs: string[], options?: { format?: string; quality?: number }) => {
    return Promise.allSettled(srcs.map(src => preloadImage(src, options)));
  }, [preloadImage]);

  return { preloadImage, preloadImages };
};

// Higher-order component for progressive image enhancement
export const withImageOptimization = <P extends object>(
  WrappedComponent: React.ComponentType<P>
) => {
  const ImageOptimizedComponent = (props: P) => {
    const { preloadImages } = useImagePreloader();
    
    useEffect(() => {
      // Preload critical images on component mount
      const criticalImages = [
        '/assets/images/hero-bg.jpg',
        '/assets/images/camera-placeholder.jpg'
      ];
      
      preloadImages(criticalImages, { format: 'webp', quality: 75 });
    }, [preloadImages]);

    return <WrappedComponent {...props} />;
  };

  ImageOptimizedComponent.displayName = `withImageOptimization(${WrappedComponent.displayName || WrappedComponent.name})`;
  
  return ImageOptimizedComponent;
};

