}

import { useEffect, useState, useCallback } from "react";
import { Link, useParams } from "react-router-dom";
import { Category, Product } from "../types/type";
import { STORAGE_BASE_URL } from "../api/constants";
import axiosInstance from "../api/axiosInstance";
import ProductCard from "../components/ProductCard";
import NavCard from "../components/navCard";
import AnimatedPulseBorder from "../components/AnimatedPulseBorder";
import ProductCardSkeleton from "../components/ProductCardSkeleton";
import BottomNavigation from "../components/BottomNavigation";

export default function CategoryDetails() {
  const { slug } = useParams<{ slug: string }>();
  const [category, setCategory] = useState<Category | null>(null);
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [totalPages, setTotalPages] = useState(1);
  const pageSize = 10;

  // Fetch category info first
  useEffect(() => {
    if (!slug) return;

    axiosInstance
      .get(`/category/${slug}`)
      .then((response) => {
        setCategory(response.data.data);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, [slug]);

  // Fetch products from API endpoint with server-side pagination
  const fetchProducts = useCallback(
    async (p = 1, append = false) => {
      if (!category?.slug) return;

      try {
        if (append) {
          setLoadingMore(true);
        } else {
          setLoading(true);
        }

        // Fetch products from API with category filter
        const response = await axiosInstance.get('/products', {
          params: {
            category: category.slug,
            page: p,
            limit: pageSize,
            exclude_rental_includes: true
          }
        });

        const data = response.data.data || [];
        const meta = response.data.meta || {};

        setTotalPages(meta.last_page || 1);
        setHasMore(p < (meta.last_page || 1));

        if (append) {
          setProducts((prev) => [...prev, ...data]);
        } else {
          setProducts(data);
        }
        setPage(p);
        setError(null);
      } catch (err: any) {
        console.error('Error fetching products for category:', err);
        setError(err.message || "Gagal memuat produk");
      } finally {
        setLoading(false);
        setLoadingMore(false);
      }
    },
    [category?.slug, pageSize]
  );

  // Load products when category is loaded
  useEffect(() => {
    if (category) {
      fetchProducts(1, false);
    }
  }, [category, fetchProducts]);

  const loadMore = () => {
    if (!hasMore || loadingMore) return;
    fetchProducts(page + 1, true);
  };

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!category) {
    return (
      <>
        <NavCard></NavCard>
        <AnimatedPulseBorder isLoading={true}>
