import { useEffect, useState, useCallback } from "react";
import BrandCard from "../components/BrandCard";
import { Brand } from "../types/type";
import axios from "axios";
import axiosInstance from "../api/axiosInstance";
import BrandCardSkeleton from "../components/BrandCardSkeleton";

export default function BrowseBrandWrapper() {
  const [brands, setBrands] = useState<Brand[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchBrands = useCallback(() => {
    const controller = new AbortController();
    
    console.log('üîÑ Fetching premiere brands...');
    setError(null); // Clear previous errors
    
    axiosInstance
      .get("/brands-premiere", {
        signal: controller.signal,
      })
      .then((response) => {
        console.log('‚úÖ Brands premiere response received:', response);
        
        // Add null/undefined checks for response data
        if (response.data && Array.isArray(response.data.data)) {
          setBrands(response.data.data);
          console.log(`üì¶ Loaded ${response.data.data.length} premiere brands`);
        } else if (response.data && Array.isArray(response.data)) {
          // Handle case where data is directly in response.data (not wrapped in data property)
          setBrands(response.data);
          console.log(`üì¶ Loaded ${response.data.length} premiere brands (direct data)`);
        } else {
          console.warn("‚ö†Ô∏è Invalid brands data structure received:", response.data);
          setBrands([]);
        }
        setLoading(false);
      })
      .catch((error) => {
        if (axios.isCancel && axios.isCancel(error)) {
          console.log("üö´ Request canceled:", error.message);
        } else {
          console.error('‚ùå Error fetching premiere brands:', error);
          
          // Use custom error message if available
          const errorMessage = (error as any).errorMessage || error.message || 'Gagal memuat brand premiere';
          setError(errorMessage);
          setLoading(false);
          
          // Set empty array as fallback
          setBrands([]);
        }
      });
    return () => controller.abort();
  }, []);

  useEffect(() => {
    const abortFetch = fetchBrands();
    return () => {
      abortFetch();
    };
  }, [fetchBrands]);

  if (loading) {
    return (
      <div className="w-auto bg-[#c5c7cb] py-4 md:py-3 px-3 md:px-3 rounded-2xl shadow-lg border border-gray-200">
        <div className="flex flex-nowrap justify-center items-center gap-2 md:gap-16 overflow-x-auto">
          {Array.from({ length: 5 }).map((_, i) => (
            <BrandCardSkeleton key={`skeleton-${i}`} />
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div
      className="w-auto bg-[#c5c7cb] py-4 md:py-3 px-3 md:px-3 scroll-fade-in rounded-2xl shadow-lg border border-gray-200"
      data-delay="100"
    >
      <div
        className="flex flex-nowrap justify-center items-center gap-2 md:gap-16 stagger-fade-in overflow-x-auto"
        data-staggerdelay="100"
      >
        {brands && Array.isArray(brands) && brands.length > 0 ? (
          brands.map((brand, index) => (
            <div key={brand.id} className="stagger-item" data-index={index}>
              <BrandCard brand={brand} />
            </div>
          ))
        ) : (
          <div className="text-center text-gray-500 py-4">
            Tidak ada brand yang tersedia
          </div>
        )}
      </div>
    </div>
  );
}

