  defaultOpen = false,
  showSelectAll = true,
}: FilterCheckboxGroupProps) {
  const handleCheckboxChange = (value: string, checked: boolean) => {
    if (checked) {
      onChange([...selectedValues, value]);
    } else {
      onChange(selectedValues.filter((v) => v !== value));
    }
  };

  const handleSelectAll = () => {
    if (selectedValues.length === options.length) {
      // Deselect all
      onChange([]);
    } else {
      // Select all
      onChange(options.map(opt => opt.value));
    }
  };

  const selectedCount = selectedValues.length;
  const allSelected = selectedCount === options.length && options.length > 0;
  const someSelected = selectedCount > 0 && selectedCount < options.length;

  return (
    <Disclosure as="div" className={`border-b border-gray-200 py-6 ${className}`} defaultOpen={defaultOpen}>
      <h3 className="-my-3 flow-root">
        <DisclosureButton className="group flex w-full items-center justify-between bg-white py-3 text-sm text-gray-400 hover:text-gray-500">
          <span className="font-medium text-gray-900 flex items-center">
            {title}
            {selectedCount > 0 && (
              <span className="ml-2 inline-flex items-center rounded-full bg-indigo-100 px-2.5 py-0.5 text-xs font-medium text-indigo-800">
                {selectedCount}
              </span>
            )}
          </span>
          <span className="ml-6 flex items-center">
            <PlusIcon
              aria-hidden="true"
              className="h-5 w-5 group-data-[open]:hidden"
            />
            <MinusIcon
              aria-hidden="true"
              className="h-5 w-5 group-[&:not([data-open])]:hidden"
            />
          </span>
        </DisclosureButton>
      </h3>
      <DisclosurePanel className="pt-6">
        <div className="space-y-4">
          {/* Select All option */}
          {showSelectAll && options.length > 0 && (
            <div className="flex items-center border-b border-gray-100 pb-3 mb-3">
              <div className="flex h-5 items-center">
                <input
                  id={`selectall-${title.toLowerCase().replace(/\s+/g, '-')}`}
                  type="checkbox"
                  checked={allSelected}
                  ref={(input) => {
                    if (input) input.indeterminate = someSelected;
                  }}
                  onChange={handleSelectAll}
                  className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                />
              </div>
              <div className="ml-3 text-sm">
                <label
                  htmlFor={`selectall-${title.toLowerCase().replace(/\s+/g, '-')}`}
                  className="font-medium text-gray-700 cursor-pointer"
                >
                  {allSelected ? 'Batalkan Semua' : 'Pilih Semua'}
                </label>
              </div>
            </div>
          )}
          
          {/* Individual options */}
          {options.map((option, optionIdx) => (
            <div key={option.value} className="flex items-center">
              <div className="flex h-5 items-center">
                <input
                  id={`filter-${title.toLowerCase().replace(/\s+/g, '-')}-${optionIdx}`}
                  type="checkbox"
                  checked={selectedValues.includes(option.value)}
                  onChange={(e) => handleCheckboxChange(option.value, e.target.checked)}
                  className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                />
              </div>
              <div className="ml-3 text-sm">
                <label
                  htmlFor={`filter-${title.toLowerCase().replace(/\s+/g, '-')}-${optionIdx}`}
                  className="text-gray-600 cursor-pointer hover:text-gray-900"
                >
                  {option.label}
                </label>
              </div>
            </div>
          ))}
          
          {options.length === 0 && (
            <p className="text-sm text-gray-500 italic">Tidak ada pilihan tersedia</p>
          )}
        </div>
      </DisclosurePanel>
    </Disclosure>
  );
}

import React from "react";
import { XMarkIcon } from "@heroicons/react/20/solid";

interface FilterData {
  category: string[];
  brand: string[];
  subcategory: string[];
  available: string[];
  q: string;
}

interface FilterHeaderProps {
  currentFilters: FilterData;
  priceRange: { min: number; max: number } | null;
  onClearAll: () => void;
  onClearCategory: (value: string) => void;
  onClearBrand: (value: string) => void;
  onClearSubcategory: (value: string) => void;
  onClearAvailable: (value: string) => void;
  onClearPrice: () => void;
  onClearSearch: () => void;
  categoryOptions: { label: string; value: string }[];
  brandOptions: { label: string; value: string }[];
  subCategoryOptions: { label: string; value: string }[];
}

export default function FilterHeader({
  currentFilters,
  priceRange,
  onClearAll,
  onClearCategory,
  onClearBrand,
  onClearSubcategory,
  onClearAvailable,
  onClearPrice,
  onClearSearch,
  categoryOptions,
  brandOptions,
  subCategoryOptions,
}: FilterHeaderProps) {
  // Calculate total active filters with null checks
  const activeFiltersCount =
    (currentFilters.category || []).length +
    (currentFilters.brand || []).length +
    (currentFilters.subcategory || []).length +
    (currentFilters.available || []).length +
    (priceRange && (priceRange.min > 0 || priceRange.max > 0) && 
     !(priceRange.min === 0 && priceRange.max === 0) ? 1 : 0) +
    (currentFilters.q ? 1 : 0);

  // Get filter items with individual remove handlers
  const getActiveFilterItems = () => {
    const items: { label: string; onRemove: () => void; type: string }[] = [];

    // Add category items with null checks
    (currentFilters.category || []).forEach((cat) => {
      const option = (categoryOptions || []).find((opt) => opt.value === cat);
      if (option) {
        items.push({
          label: option.label,
          onRemove: () => onClearCategory(cat),
          type: "category",
        });
      }
    });

    // Add brand items with null checks
    (currentFilters.brand || []).forEach((brand) => {
      const option = (brandOptions || []).find((opt) => opt.value === brand);
      if (option) {
        items.push({
          label: option.label,
          onRemove: () => onClearBrand(brand),
          type: "brand",
        });
      }
    });

    // Add subcategory items with null checks
    (currentFilters.subcategory || []).forEach((sub) => {
      const option = (subCategoryOptions || []).find((opt) => opt.value === sub);
      if (option) {
        items.push({
          label: option.label,
          onRemove: () => onClearSubcategory(sub),
          type: "subcategory",
        });
      }
    });

    // Add availability items with null checks
    (currentFilters.available || []).forEach((avail) => {
      items.push({
        label: avail === "1" ? "Tersedia" : "Tidak Tersedia",
        onRemove: () => onClearAvailable(avail),
        type: "availability",
      });
    });

    // Add price range (only if both values are meaningful and not default 0-0)
    if (priceRange && (priceRange.min > 0 || priceRange.max > 0) && 
        !(priceRange.min === 0 && priceRange.max === 0)) {
      const minFormatted = priceRange.min.toLocaleString("id-ID");
      const maxFormatted = priceRange.max.toLocaleString("id-ID");
      items.push({
        label: `Harga: Rp ${minFormatted} - Rp ${maxFormatted}`,
        onRemove: onClearPrice,
        type: "price",
      });
    }

    // Add search query
    if (currentFilters.q) {
      items.push({
        label: `"${currentFilters.q}"`,
        onRemove: onClearSearch,
        type: "search",
      });
    }

    return items;
  };

  const activeItems = getActiveFilterItems();

  if (activeFiltersCount === 0) {
    return null;
  }

  return (
    <div className="mb-4 p-3 bg-blue-50/50 rounded-lg border border-blue-200">
      {/* Header with title and clear all button */}
      <div className="flex items-center justify-between mb-2">
        <h3 className="text-sm font-medium text-gray-800">
          Filter Aktif ({activeFiltersCount})
        </h3>
        <button
          onClick={onClearAll}
          className="inline-flex items-center gap-1 px-2 py-1 text-xs font-medium text-red-600 bg-white border border-red-200 rounded-md hover:bg-red-50 hover:border-red-300 transition-colors"
        >
          Hapus Semua
        </button>
      </div>

      {/* Active filter chips with individual remove buttons */}
      <div className="flex flex-wrap gap-2">
        {activeItems.map((item, index) => (
          <span
            key={`${item.type}-${index}`}
            className="inline-flex items-center gap-1 rounded-full bg-blue-100 px-2 py-1 text-xs font-medium text-blue-800 border border-blue-200"
          >
            <span className="truncate max-w-xs">{item.label}</span>
            <button
              onClick={item.onRemove}
              className="inline-flex items-center justify-center w-3 h-3 rounded-full hover:bg-blue-200 text-blue-600 hover:text-blue-800 transition-colors flex-shrink-0"
              title={`Hapus filter ${item.label}`}
            >
              <XMarkIcon className="w-2.5 h-2.5" />
            </button>
          </span>
        ))}
      </div>
    </div>
  );
}

import React from "react";
import {
  Disclosure,
  DisclosureButton,
  DisclosurePanel,
} from "@headlessui/react";
import { PlusIcon, MinusIcon } from "@heroicons/react/20/solid";
import ReactSelect from "react-select";

interface FilterOption {
  label: string;
  value: string;
}

interface FilterSectionProps {
  title: string;
  options: FilterOption[];
  selectedValues: string[];
  onChange: (values: string[]) => void;
  placeholder?: string;
  isMulti?: boolean;
  className?: string;
}

const rsStyles = {
  control: (base: any) => ({
    ...base,
    backgroundColor: "transparent",
    borderColor: "rgb(209 213 219)", // gray-300
    boxShadow: "none",
    minHeight: "40px",
    "&:hover": { borderColor: "rgb(156 163 175)" }, // gray-400
  }),
  menu: (base: any) => ({ ...base, zIndex: 9999 }),
  option: (base: any, { isSelected, isFocused }: any) => ({
    ...base,
    backgroundColor: isSelected
      ? "rgb(99 102 241)" // indigo-600
      : isFocused
      ? "rgb(238 242 255)" // indigo-50
      : "white",
    color: isSelected ? "white" : "rgb(17 24 39)", // gray-900
  }),
};

export default function FilterSection({
  title,
  options,
  selectedValues,
  onChange,
  placeholder = "Select options",
  isMulti = true,
  className = "",
}: FilterSectionProps) {
  const handleChange = (selected: any) => {
    if (isMulti) {
      onChange(selected ? selected.map((s: any) => s.value) : []);
    } else {
      onChange(selected ? [selected.value] : []);
    }
  };

  const selectedOptions = options.filter((opt) =>
    selectedValues.includes(opt.value)
  );

  return (
    <Disclosure as="div" className={`border-b border-gray-200 py-6 ${className}`}>
      <h3 className="-my-3 flow-root">
        <DisclosureButton className="group flex w-full items-center justify-between bg-white py-3 text-sm text-gray-400 hover:text-gray-500">
          <span className="font-medium text-gray-900">{title}</span>
          <span className="ml-6 flex items-center">
            <PlusIcon
              aria-hidden="true"
              className="h-5 w-5 group-data-[open]:hidden"
            />
            <MinusIcon
              aria-hidden="true"
              className="h-5 w-5 group-[&:not([data-open])]:hidden"
            />
          </span>
        </DisclosureButton>
      </h3>
      <DisclosurePanel className="pt-6">
        <ReactSelect
          isMulti={isMulti}
          options={options}
          value={selectedOptions}
          onChange={handleChange}
          styles={rsStyles}
          placeholder={placeholder}
          className="text-sm"
        />
      </DisclosurePanel>
    </Disclosure>
  );
}

import React from "react";
import { Dialog, DialogBackdrop, DialogPanel } from "@headlessui/react";
import { XMarkIcon } from "@heroicons/react/24/outline";
import FilterCheckboxGroup from "./FilterCheckboxGroup";
import FilterHeader from "./FilterHeader";
import PriceRange from "../PriceRange";

interface FilterOption {
  label: string;
  value: string;
}

interface FilterData {
  category: string[];
  brand: string[];
  subcategory: string[];
  available: string[];
  q: string;
}

interface PriceRangeData {
  min: number;
  max: number;
}

interface MobileFilterDialogProps {
  isOpen: boolean;
  onClose: () => void;
  tempFilter: FilterData;
  setTempFilter: React.Dispatch<React.SetStateAction<FilterData>>;
  tempPriceRange: PriceRangeData | null;
  setTempPriceRange: React.Dispatch<
    React.SetStateAction<PriceRangeData | null>
  >;
  priceBounds: PriceRangeData;
  categoryOptions: FilterOption[];
  brandOptions: FilterOption[];
  subCategoryOptions: FilterOption[];
  onApply: () => void;
  onReset: () => void;
}

export default function MobileFilterDialog({
  isOpen,
  onClose,
  tempFilter,
  setTempFilter,
  tempPriceRange,
  setTempPriceRange,
  priceBounds,
  categoryOptions,
  brandOptions,
  subCategoryOptions,
  onApply,
  onReset,
}: MobileFilterDialogProps) {
  const handleFilterChange = (key: keyof FilterData) => (values: string[]) => {
    setTempFilter((prev) => ({ ...prev, [key]: values }));
  };

  return (
    <Dialog open={isOpen} onClose={onClose} className="relative z-40 lg:hidden">
      <DialogBackdrop
        transition
        className="fixed inset-0 bg-black/25 transition-opacity duration-300 ease-linear data-[closed]:opacity-0"
      />

      <div className="fixed inset-0 z-40 flex">
        <DialogPanel
          transition
          className="relative ml-auto flex h-full max-w-xs transform flex-col overflow-y-auto bg-white py-4 pb-6 shadow-xl transition duration-300 ease-in-out data-[closed]:translate-x-full w-full"
        >
          <div className="flex items-center justify-between px-4">
            <h2 className="text-xs font-extralight text-gray-900">Filters</h2>
            <button
              type="button"
              onClick={onClose}
              className="relative -mr-2 flex h-10 w-10 items-center justify-center rounded-md bg-white p-2 text-gray-400 hover:bg-gray-50 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
            >
              <span className="absolute -inset-0.5" />
              <span className="sr-only">Close menu</span>
              <XMarkIcon aria-hidden="true" className="h-6 w-6" />
            </button>
          </div>

          <div className="mt-4 px-4">
            {/* Filter Header with Clear All */}
            <FilterHeader
              currentFilters={tempFilter}
              priceRange={tempPriceRange}
              onClearAll={onReset}
              onClearCategory={(value) => {
                setTempFilter((prev) => ({
                  ...prev,
                  category: prev.category.filter((c) => c !== value),
                }));
              }}
              onClearBrand={(value) => {
                setTempFilter((prev) => ({
                  ...prev,
                  brand: prev.brand.filter((b) => b !== value),
                }));
              }}
              onClearSubcategory={(value) => {
                setTempFilter((prev) => ({
                  ...prev,
                  subcategory: prev.subcategory.filter((s) => s !== value),
                }));
              }}
              onClearAvailable={(value) => {
                setTempFilter((prev) => ({
                  ...prev,
                  available: prev.available.filter((a) => a !== value),
                }));
              }}
              onClearPrice={() => setTempPriceRange(null)}
              onClearSearch={() => {
                setTempFilter((prev) => ({ ...prev, q: "" }));
              }}
              categoryOptions={categoryOptions}
              brandOptions={brandOptions}
              subCategoryOptions={subCategoryOptions}
            />
          </div>

          <form className="border-t border-gray-200 px-4">
            <FilterCheckboxGroup
              title="Kategori"
              options={categoryOptions}
              selectedValues={tempFilter.category}
              onChange={handleFilterChange("category")}
              className="border-t-0 pt-0"
              defaultOpen={true}
            />

            <FilterCheckboxGroup
              title="Sub Kategori"
              options={subCategoryOptions}
              selectedValues={tempFilter.subcategory}
              onChange={handleFilterChange("subcategory")}
              defaultOpen={false}
            />

            <FilterCheckboxGroup
              title="Brand"
              options={brandOptions}
              selectedValues={tempFilter.brand}
              onChange={handleFilterChange("brand")}
              defaultOpen={false}
            />

            <FilterCheckboxGroup
              title="Status Ketersediaan"
              options={[
                { label: "Tersedia", value: "1" },
                { label: "Tidak Tersedia", value: "0" },
              ]}
              selectedValues={tempFilter.available}
              onChange={handleFilterChange("available")}
              defaultOpen={false}
              showSelectAll={false}
            />

            <div className="border-b border-gray-200 py-6">
              <h3 className="mb-4 text-sm font-medium text-gray-900">
                Rentang Harga
              </h3>
              <PriceRange
                min={priceBounds.min}
                max={priceBounds.max}
                valueMin={tempPriceRange?.min ?? null}
                valueMax={tempPriceRange?.max ?? null}
                onChange={(min, max) => {
                  setTempPriceRange(
                    min == null && max == null
                      ? null
                      : {
                          min: min ?? priceBounds.min,
                          max: max ?? priceBounds.max,
                        }
                  );
                }}
              />
            </div>

            <div className="flex gap-3 mt-6">
              <button
                type="button"
                onClick={onApply}
                className="flex-1 bg-text-light-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors"
              >
                Terapkan
              </button>
              <button
                type="button"
                onClick={onReset}
                className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors"
              >
                Reset
              </button>
            </div>
          </form>
        </DialogPanel>
      </div>
    </Dialog>
  );
}
